select	dategen.date, ( select sum(case when memid = 0 then slots * fac.guestcost else slots * membercost end) as rev from cd.bookings bk inner join cd.facilities fac on bk.facid = fac.facid where bk.starttime > dategen.date - interval '14 days' and bk.starttime < dategen.date + interval '1 day' )/15 as revenue from ( select	cast(generate_series(timestamp '2012-08-01', '2012-08-31','1 day') as date) as date ) as dategen order by dategen.date